mapper.select:
SELECT COUNT(*) 
FROM CART 
WHERE ITEM_KEY=#{itemKey} 
AND MEMBER_ID=#{memberId} 
AND CART_STATUS='N'

mapper.insert:
INSERT INTO CART 
VALUES (
	SEQ_CKEY.NEXTVAL
	, #{cartQuantity}
	, #{cartStatus}
	, SYSDATE
	, #{itemKey}
	, #{memberId} 
)

mapper.update:
UPDATE CART 
SET CART_QUANTITY=#{cartQuantity} 
WHERE ITEM_KEY=#{itemKey}
